import React from 'react';
import './App.css';

function App() {
  return (
    <div className="root-view">
      <div className="rm-toolbar">
        <div className="logo">ICloud미리 알림</div>
        <div className="gnb">김진범</div>
      </div>
      <MainView></MainView>
    </div>
  );
}

class MainView extends React.Component {
  constructor(props) {
    super(props);
    this.handleTitle = this.handleTitle.bind(this);
    this.state = {
      title: '',
    }
  }

  handleTitle(title) {
    this.setState({title: title});
  }

  render() {
    return (
      <div className="main-view">
        <Sidebar onTitle={this.handleTitle}></Sidebar>
        <RmList title={this.state.title}></RmList>
      </div>
    );
  }
}

class Sidebar extends React.Component {
  constructor(props) {
    super(props);
    this.rmList = Array.of(
      1,
      2,
      3
    );
  }

  showRmList() {
    return (
      this.rmList.map(x => {
        return(
          <li onClick={() => this.props.onTitle(x)}>{x}</li>
        )
      })
    );
  }

  render() {    
    return (
      <div className="sidebar">
        <ul>
          {this.showRmList()}
        </ul>
      </div>
    );
  }
}

class RmList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      rmItem: [],
    }
  }

  render() {
    return(
      <div className="rm-list">
        <div className="rm-list-header">
          <div className="inline-editable-label" role="textbox">
            {this.props.title}
          </div>
        </div>
        <div className="rm-list-body">
          <RmItem></RmItem>
        </div>
        <div className="rm-list-footer" onClick={() => alert('클릭됨')}>
          새로운 미리 알림
        </div>
      </div>
    );
  }
}

class RmItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      checked: false
    }

    this.handleChange = this.handleChange.bind(this);
  }

  handleClick() {
    const state = this.state;
    state.checked = !state.checked;
    this.setState(state);
  }

  handleChange(event) {
    this.setState({title: event.target.value});
  }

  render() {
    return (
      <div className="rm-item">
        <div className={`rm-item-checkable ${this.state.checked ? 'checked' : ''}`} onClick={() => this.handleClick()}></div>
        <form className="rm-item-title">
           <input value={this.state.title} onChange={this.handleChange}></input>
        </form>
      </div>
    );
  }
}

export default App;
