import React from 'react';
import './App.css';

class RmListMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isSelected: false
    }
  }

  isSelectedClass() {
    return this.state.isSelected ? "is-selected" : "";
  }

  handleClick() {
    this.setState({isSelected: !this.state.isSelected});
  }

  render() {
    return (
      <div className={`rm-list-menu-item ${this.isSelectedClass()}`}
        onClick={() => this.handleClick()}
      >
        <div className="rm-list-menu-item-content">{this.props.title}</div>
      </div>
    );
  }
}

class App extends React.Component {
  constructor(prop) {
    super(prop);

    this.state = {
      selectedRmListMenu: null,
      rmListMenuList: []
    }

    this.handleAddList = this.handleAddList.bind(this);
  }

  handleSelectedRmListMenu(rmListMenu) {
    this.setState({selectedRmListMenu: rmListMenu});
  }

  handleAddList() {
    this.setState({rmListMenuList: this.state.rmListMenuList.concat(
      <RmListMenu title="New List"></RmListMenu>
    )});
  }

  render() {
    return (
      <div className="root-view">
        <div className="rm-toolbar">
          <div className="logo">ICloud Reminders</div>
          <div className="gnb">김진범</div>
        </div>
        <div className="main-view">
          <div className="sidebar">
            <div className="scrollable-area">
              <div className="rm-list-menu">
                {this.state.rmListMenuList}
              </div>
            </div>
            <div className="sidebar-footer" onClick={() => this.handleAddList()}>
              Add List
            </div>
          </div>
          <div className="rm-list">
            <div className="rm-list-header">
                New List
            </div>
            <div className="rm-list-body"></div>
            <div className="rm-list-footer">
              Add Reminder
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
