import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(prop) {
    super(prop);
    this.state = {
      selectedList: "",
      rmListItems: []
    }
    this.init();
  }

  init() {
    const newRmListItems = Array(3).fill("New List");
    this.setState({rmListItems: newRmListItems});
  }

  render() {
    const rmListItems = this.state.rmListItems.map((rmListItem, index) => {
      let selected = false;
      return (
        <div key={index} className={`rm-list-menu-item ${selected ? '' : 'selected'}`} onClick={() => {
          const newState = Object.assign({}, this.state, {selectedList: rmListItem});
          this.setState(newState);
          selected = true;
          }}>
          <div className="rm-list-menu-item-content">{rmListItem}</div>
        </div>
      );
    }, this);

    return (
      <div className="root-view">
        <div className="rm-toolbar">
          <div className="logo">ICloud미리 알림</div>
          <div className="gnb">김진범</div>
        </div>
        <div class="main-view">
          <div className="sidebar">
            <div className="scrollable-area">
              <div className="rm-list-menu">
                {rmListItems}
              </div>
            </div>
          </div>
          <div className="rm-list">
            <div className="rm-list-header">
                New List
            </div>
            <div className="rm-list-body">
            </div>
            <div className="rm-list-footer">
              새로운 미리 알림
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
