import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(prop) {
    super(prop);

    this.state = {
      selectedRmListMenu: null,
      rmListMenuList: []
    }

    this.handleAddList = this.handleAddList.bind(this);
  }

  handleAddList() {
    this.setState({rmListMenuList: this.state.rmListMenuList.concat("New List")});
  }

  render() {
    const rmListMenuList = this.state.rmListMenuList.map((rmListMenu, idex) => {
      let isSelected = true;

      return (
        <div key={idex} className={`rm-list-menu-item ${isSelected ? "selected" : ""}`} onClick={(() => {
          isSelected = !isSelected;
        })}>
          <div className="rm-list-menu-item-content">{rmListMenu}</div>
        </div>
      );
    });
    return (
      <div className="root-view">
        <div className="rm-toolbar">
          <div className="logo">ICloud Reminders</div>
          <div className="gnb">김진범</div>
        </div>
        <div className="main-view">
          <div className="sidebar">
            <div className="scrollable-area">
              <div className="rm-list-menu">
                {rmListMenuList}
              </div>
            </div>
            <div className="sidebar-footer" onClick={() => this.handleAddList()}>
              Add List
            </div>
          </div>
          <div className="rm-list">
            <div className="rm-list-header">
                New List
            </div>
            <div className="rm-list-body"></div>
            <div className="rm-list-footer">
              Add Reminder
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
